import ArticleCard from 'src/components/molecules/ArticleCard'
import { Grid } from '@material-ui/core'
import { useQuery } from 'react-query'
const contentful = require('contentful')

const client = contentful.createClient({
  space: 'xoss9eel4gbp',
  accessToken: 'Yr2amP-j26-y2aSd3ojy12BoWDeJqENVd9RjWOVNHiY'
})

const ArticleArea: React.FC = () => {
  const { isLoading, data: articles } = useQuery('articles', async () => {
    return await client.getEntries()
  })
  !isLoading && console.log(articles)
  return (
    <div>
      <Grid container spacing={1} justify="center" alignItems="center">
        {articles.includes.Asset.map((article) => (
          <Grid container item xs={4}>
            <ArticleCard articleTitle={article.fields.title} articleBody={article.fields.body} />
          </Grid>
        ))}
      </Grid>
      {/* <Grid container spacing={1} justify="center" alignItems="center">
