import React from 'react'
import Link from 'next/link'
import { makeStyles, Theme, createStyles } from '@material-ui/core/styles'
import Card from '@material-ui/core/Card'
import CardActions from '@material-ui/core/CardActions'
import CardHeader from '@material-ui/core/CardHeader'
import CardContent from '@material-ui/core/CardContent'
import CardMedia from '@material-ui/core/CardMedia'
import Avatar from '@material-ui/core/Avatar'
import Typography from '@material-ui/core/Typography'
import { red } from '@material-ui/core/colors'
import IconButton from '@material-ui/core/IconButton'
import MoreVertIcon from '@material-ui/icons/MoreVert'
import FavoriteIcon from '@material-ui/icons/Favorite'
import ShareIcon from '@material-ui/icons/Share'
const contentful = require('contentful')

const client = contentful.createClient({
  space: 'xoss9eel4gbp',
  accessToken: 'Yr2amP-j26-y2aSd3ojy12BoWDeJqENVd9RjWOVNHiY'
})

const getArticleData = async () => {
  return await client.getEntry('5egc7gXK25PoEoI16XBuN6')
}

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      margin: 30,
      maxWidth: 1000
    },
    media: {
      height: 0,
      paddingTop: '56.25%' // 16:9
    },
    avatar: {
      backgroundColor: red[500]
    }
  })
)

const ArticleCard: React.FC = async () => {
  const classes = useStyles()
  const hoge = await getArticleData()
  console.log(hoge)
  return (
    <Card className={classes.root}>
      <CardHeader
        avatar={
          <Avatar aria-label="recipe" className={classes.avatar}>
            Y
          </Avatar>
        }
        action={
          <IconButton aria-label="settings">
            <MoreVertIcon />
          </IconButton>
        }
        title="バイク納車！！"
        subheader="2020/11/28"
      />
      <CardMedia className={classes.media} image="../../static/bike.jpg" title="bike" />
      <Link href="/articleDetail">
        <CardContent>
          <Typography variant="body2" color="textSecondary" component="p">
            ついにバイク納車！！
          </Typography>
        </CardContent>
      </Link>
      <CardActions disableSpacing>
        <IconButton aria-label="add to favorites">
          <FavoriteIcon />
        </IconButton>
        <IconButton aria-label="share">
          <ShareIcon />
        </IconButton>
      </CardActions>
    </Card>
  )
}
export default ArticleCard
